// Lightweight loader to include Bootstrap 3 JS from CDN.
// This avoids bundling Bootstrap source in the repo; replace with local file if desired.
(function(){
  var url = 'https://maxcdn.bootstrapcdn.com/bootstrap/3.3.7/js/bootstrap.min.js';
  var s = document.createElement('script');
  s.src = url;
  s.crossOrigin = 'anonymous';
  s.onload = function(){ console.log('Bootstrap JS loaded from CDN'); };
  s.onerror = function(){ console.warn('Unable to load Bootstrap from CDN.'); };
  document.head.appendChild(s);
})();
